import React from "react";
import { Link } from "react-router-dom";
import { useState } from "react";

function ResetMatKhau() {
    const [isTokenValid, setIsTokenValid] = useState(false);
    const [newPassword, setNewPassword] = useState("");
    const [repeatNewPassword, setRepeatNewPassword] = useState("");
    const [msgErr, setMsgErr] = useState(null);
    const [msgSuccess, setMsgSuccess] = useState(null);
    const queryParameters = new URLSearchParams(window.location.search)
    const token = queryParameters.get("token");
    const handleResetPassword = async (e) => {
        const checkValidToken = async (token) => {
            if (!isTokenValid) {
                // Token is not valid, handle accordingly
                return;
            }
            else {
                const requestForgot = {
                    newPassword: newPassword,
                    repeatNewPassword: repeatNewPassword
                };
                try {
                    const response = await fetch('http://localhost:8000/v1/user/reset-password', {
                        method: 'PUT',
                        body: JSON.stringify(requestForgot),
                        headers: {
                            'Content-Type': 'application/json',
                            'Authorization': `Bearer ${token}`
                        }
                    });

                    if (!response.ok) {
                        const errorData = await response.json();
                        setMsgErr(errorData.message);

                    } else {
                        setMsgErr("Thay đổi mật khẩu thành công !");
                    }
                } catch (error) {
                    setMsgErr("Đã xảy ra lỗi không mong muốn. Vui lòng thử lại.");
                    console.log("ResetPassword Error: ", error);
                }
            }
        }

    }
    return (
        <>
            {/* Navbar */}
            <div className="container-fluid bg-light position-relative shadow">
                <nav className="navbar navbar-expand-lg bg-light navbar-light py-3 py-lg-0 px-0 px-lg-5 position-relative">
                    <a href="/" className="navbar-brand font-weight-bold text-secondary" style={{ fontSize: 50 }}>
                        <img src="img/logo.png" alt="Logo"></img>
                        <span className="text-primary" style={{ fontSize: 40 }}> BloodnHeart</span>
                    </a>
                    <button type="button" className="navbar-toggler" data-toggle="collapse" data-target="#navbarCollapse">
                        <span className="navbar-toggler-icon" />
                    </button>
                    <div className="collapse navbar-collapse justify-content-between" id="navbarCollapse">
                        <div className="navbar-nav font-weight-bold mx-auto py-0">
                            {/* You can add navigation links here if needed */}
                        </div>
                        <a href="/" className="btn btn-primary m-3 position-absolute" style={{ right: "0px" }}>
                            QUAY LẠI <i className="fa fa-arrow-right"></i>
                        </a>
                    </div>
                </nav>
            </div>
            {/* Navbar End */}

            {/* Change Password Form */}
            <div className="container-fluid pt-5">
                <div className="container">
                    <div className="text-center pb-2">
                        <p className="section-title px-5">
                            <span className="px-2">THAY ĐỔI MẬT KHẨU</span>
                        </p>
                        <h2 className="mb-2 text-primary">CẬP NHẬT MẬT KHẨU MỚI</h2>
                    </div>
                </div>
                {isTokenValid ? (
                    <form
                        onSubmit={handleResetPassword}
                        className="col-6 mx-auto d-flex flex-column align-items-center">
                        <div className="form-group col-lg-8">
                            <label className="form-control-label mb-2">Nhập mật khẩu mới</label>
                            <input type="password" className="form-control p-2"
                                onChange={(e) => setNewPassword(e.target.value)} />
                        </div>
                        <div className="form-group col-lg-8">
                            <label className="form-control-label mb-2">Xác nhận mật khẩu</label>
                            <input type="password" className="form-control p-2"
                                onChange={(e) => setRepeatNewPassword(e.target.value)} />
                        </div>
                        <div className="col-lg-6">
                            <div className="col-lg-6">
                                {/* Error Message */}
                            </div>
                            <div className="col-lg-12 d-flex justify-content-center align-items-center">
                                <button type="submit" className="btn btn-outline-primary mb-2 ">
                                    XÁC NHẬN <i className="fa-solid fa-arrow-right"></i>
                                </button>
                            </div>
                        </div>
                    </form>
                ) : (
                    <div className="form-control p-2 alert alert-danger" role="alert">
                        Token hết hạn hoặc không hợp lệ.
                    </div>
                )}

                <div className="col-lg-12 login-btm text-center">
                    <h6 className="mb-2">
                        <Link to="/dangky">Đăng ký tài khoản mới.</Link>
                    </h6>
                    <h6 className="mb-2">
                        Đã có tài khoản? <Link to="/dangnhap">Đăng nhập ngay </Link>
                    </h6>
                </div>
            </div>



        </>
    );
}

export default ResetMatKhau;
