import { createSlice } from "@reduxjs/toolkit";

const userSlice = createSlice({
    name: "user",
    initialState: {
        profile:{
            getUser: null,
            isFetching: false,
            error: false
        },
        profile1:{
            getUser1: null
        },
    },
    reducers: {
        userprofileStart: (state) => {
            state.profile.isFetching = true;
        },
        userprofileSuccess: (state, action) => {
            state.profile.isFetching = false;
            state.profile.getUser = action.payload;
            state.profile.error = false;
        },
        userprofileFailed: (state) => {
            state.profile.isFetching = false;
            state.profile.error = true;
        },
        userup1Start: (state) => {
            state.profile1.isFetching = true;
        },
        userup1Success: (state, action) => {
            state.profile1.isFetching = false;
            state.profile1.getUser1 = action.payload;
            state.profile1.error = false;
        },
        userup1Failed: (state) => {
            state.profile1.isFetching = false;
            state.profile1.error = true;
        },
    }
});

export const {
    userprofileStart,
    userprofileSuccess,
    userprofileFailed,
    userup1Start,
    userup1Success,
    userup1Failed
} = userSlice.actions;

export default userSlice.reducer;
